    private void btnEGLMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnEGLMouseClicked
        // TODO add your handling code here:
        double answer = 0;
        if(operator == "plus") {
            answer = value1 + value2;
        } else if(operator == "minus") {
            answer = value1 - value2;
        } else if(operator == "X") {
            answer = value1 * value2;
        } else if(operator == "imp") {
            answer = value1 / value2;
        }
        
        String Result = Double.toString(answer);
        txtResult.setText(Result);
    }//GEN-LAST:event_btnEGLMouseClicked

